import * as firebase from "firebase";
import "firebase/firestore";
import { Alert } from "react-native";

export async function registration(email, password, lastName, firstName) {
  try {
    await firebase.auth().createUserWithEmailAndPassword(email, password);
  } catch (err) {
    Alert.alert("There is something wrong!!!!", err.message);
  }
}

export async function signIn(email, password) {
  try {
    const response = await firebase
      .auth()
      .signInWithEmailAndPassword(email, password)
      .then((user) => {
        Alert.alert("Logged in as", user.user.uid);
        return user.user.uid;
      });
    return response;
  } catch (err) {
    // Alert.alert("There is something wrong!", JSON.parse(err.message).error);
    return JSON.parse(err.message).error.code;
  }
}

export async function loggingOut() {
  try {
    await firebase.auth().signOut();
  } catch (err) {
    Alert.alert("There is something wrong!", err.message);
  }
}

export async function currentUser() {
  try {
    const response = await firebase.auth().onAuthStateChanged((user) => {
      if (user) {
        Alert.alert("User ID", user.uid);
        return user.uid;
      }
    });
    return response;
  } catch (err) {
    Alert.alert("There is something wrong!", err.message);
    return JSON.parse(err.message).error.code;
  }
}
